let button=document.querySelector("button"),inputs=document.querySelectorAll(".input"),input=document.querySelector("form span");const focarInput=({target:t})=>{let e=t.previousElementSibling;e.classList.add("active")};inputs.forEach(t=>{t.addEventListener("focus",focarInput),t.style.paddingTop="32px"});const desfocarInput=({target:t})=>{if(""===t.value){let e=t.previousElementSibling;e.classList.remove("active")}};inputs.forEach(t=>{t.addEventListener("focusout",desfocarInput)});const mudarInput=()=>{let[t,e]=inputs;t.value&&e.value.length>8?button.removeAttribute("disabled"):button.setAttribute("disabled","")};inputs.forEach(t=>{t.addEventListener("input",mudarInput)});